        this.ionViewDidEnterHandler.bind(this)
      );
      this.ionPageElementRef.current.addEventListener(
        'ionViewWillLeave',
        this.ionViewWillLeaveHandler.bind(this)
      );
      this.ionPageElementRef.current.addEventListener(
        'ionViewDidLeave',
        this.ionViewDidLeaveHandler.bind(this)
